import axios from 'axios';

// Base URL del API:
// - Si VITE_API_BASE_URL está definido, se usa ese valor.
// - En desarrollo, por defecto apunta al backend local en 4001.
// - En producción, por defecto usa ruta relativa '/api'.
export const API_BASE =
  import.meta.env.VITE_API_BASE_URL || (import.meta.env.DEV ? 'http://localhost:4001/api' : '/api');

const api = axios.create({
  baseURL: API_BASE,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Interceptor para agregar token de autenticación
api.interceptors.request.use(
  (config) => {
    const token = (typeof window !== 'undefined' && window.sessionStorage.getItem('token')) || localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Interceptor para manejar errores de respuesta
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    if (error.response?.status === 401) {
      // Limpiar ambas ubicaciones de almacenamiento
      try {
        if (typeof window !== 'undefined') {
          window.sessionStorage.removeItem('token');
        }
      } catch (_) {}
      localStorage.removeItem('token');
      window.location.href = '/panel/login';
    }
    return Promise.reject(error);
  }
);

export default api;
